trigger.body:
PARTNERID VARCHAR(32);
RATIN VARCHAR(60);
BEGIN
	PARTNERID := :NEW.C_BPARTNER_ID;


	IF PARTNERID IS NOT NULL THEN

		SELECT EM_PMB_RATINGSPARTNER INTO RATIN FROM C_BPARTNER WHERE C_BPARTNER_ID = PARTNERID;

		--RAISE ratin;

		IF RATIN IS NOT NULL THEN

			 :NEW.RATINGS = RATIN;
			 --RAISE NO_DATA_FOUND||RATIN;
		END IF;
	END IF;

RETURN NEW; 
END PMB_RATINGSAUTHOR
